import { browser } from '$app/environment';
import type {
	ApiResponse,
	PaginatedResponse,
	LoginRequest,
	RegisterRequest,
	AuthResponse,
	User,
	Server,
	Channel,
	Message
} from '$lib/types';
import { getStorageItem, setStorageItem, removeStorageItem } from '$lib/utils';

// Use relative URLs in browser to leverage Vite's proxy, fallback to direct URL
const API_BASE_URL = browser ? '' : (import.meta.env.PUBLIC_API_URL || 'http://localhost:8081');

class ApiError extends Error {
	constructor(
		public status: number,
		message: string,
		public data?: unknown
	) {
		super(message);
		this.name = 'ApiError';
	}
}

class ApiClient {
	public baseUrl: string;
	private token: string | null = null;

	constructor(baseUrl?: string) {
		this.baseUrl = baseUrl || API_BASE_URL;
	}

	// Token management
	setToken(token: string): void {
		this.token = token;
	}

	clearToken(): void {
		this.token = null;
	}

	getToken(): string | null {
		return this.token;
	}

	// HTTP request helper
	private async request<T>(
		endpoint: string,
		options: RequestInit = {}
	): Promise<T> {
		const url = `${this.baseUrl}${endpoint}`;
		const headers: Record<string, string> = {
			'Content-Type': 'application/json',
			...options.headers as Record<string, string>
		};

		if (this.token) {
			headers.Authorization = `Bearer ${this.token}`;
		}

		const config: RequestInit = {
			...options,
			headers
		};

		try {
			const response = await fetch(url, config);

			if (!response.ok) {
				let errorMessage = `HTTP ${response.status}`;
				let errorData: unknown = null;

				try {
					const errorResponse = await response.json();
					errorMessage = errorResponse.message || errorMessage;
					errorData = errorResponse;
				} catch {
					// If error response is not JSON, use status text
					errorMessage = response.statusText || errorMessage;
				}

				throw new ApiError(response.status, errorMessage, errorData);
			}

			// Handle empty responses
			const contentType = response.headers.get('content-type');
			if (contentType && contentType.includes('application/json')) {
				return await response.json();
			}

			return {} as T;
		} catch (error) {
			if (error instanceof ApiError) {
				throw error;
			}
			throw new ApiError(0, 'Network error', error);
		}
	}

	// Authentication endpoints
	async login(credentials: LoginRequest): Promise<AuthResponse> {
		return this.request<AuthResponse>('/api/auth/login', {
			method: 'POST',
			body: JSON.stringify(credentials)
		});
	}

	async register(userData: RegisterRequest): Promise<AuthResponse> {
		return this.request<AuthResponse>('/api/auth/register', {
			method: 'POST',
			body: JSON.stringify(userData)
		});
	}

	async logout(): Promise<void> {
		await this.request<void>('/api/auth/logout', {
			method: 'POST'
		});
	}

	async refreshToken(): Promise<AuthResponse> {
		return this.request<AuthResponse>('/api/auth/refresh', {
			method: 'POST'
		});
	}

	async getCurrentUser(): Promise<User> {
		return this.request<User>('/api/auth/me');
	}

	async guestLogin(): Promise<AuthResponse> {
		return this.request<AuthResponse>('/api/auth/guest', {
			method: 'POST'
		});
	}

	// User endpoints
	async getUserProfile(): Promise<User> {
		return this.request<User>('/api/user/profile');
	}

	async updateUserProfile(updates: Partial<User>): Promise<User> {
		return this.request<User>('/api/user/profile', {
			method: 'PUT',
			body: JSON.stringify(updates)
		});
	}

	// Server endpoints
	async getServers(): Promise<Server[]> {
		const response = await this.request<{ servers: Server[] }>('/api/servers');
		return response.servers;
	}

	async getServer(serverId: number): Promise<Server> {
		return this.request<Server>(`/api/servers/${serverId}`);
	}

	async createServer(serverData: Partial<Server>): Promise<Server> {
		return this.request<Server>('/api/servers', {
			method: 'POST',
			body: JSON.stringify(serverData)
		});
	}

	async updateServer(serverId: number, updates: Partial<Server>): Promise<Server> {
		return this.request<Server>(`/api/servers/${serverId}`, {
			method: 'PUT',
			body: JSON.stringify(updates)
		});
	}

	async deleteServer(serverId: number): Promise<void> {
		await this.request<void>(`/api/servers/${serverId}`, {
			method: 'DELETE'
		});
	}

	// Channel endpoints
	async getChannels(serverId: number): Promise<Channel[]> {
		const response = await this.request<{ channels: any[] }>(`/api/servers/${serverId}/channels`);
		// Map the API response to match the frontend Channel interface
		return response.channels.map(channel => ({
			...channel,
			type: channel.channel_type, // Map channel_type to type
			serverId: serverId
		}));
	}

	async getServerUsers(serverId: number): Promise<User[]> {
		const response = await this.request<{ success: boolean; data: User[] }>(`/api/servers/${serverId}/users`);
		return response.data;
	}

	async createChannel(serverId: number, channelData: Partial<Channel>): Promise<Channel> {
		return this.request<Channel>(`/api/servers/${serverId}/channels`, {
			method: 'POST',
			body: JSON.stringify(channelData)
		});
	}

	async updateChannel(channelId: number, updates: Partial<Channel>): Promise<Channel> {
		return this.request<Channel>(`/api/channels/${channelId}`, {
			method: 'PUT',
			body: JSON.stringify(updates)
		});
	}

	async deleteChannel(channelId: number): Promise<void> {
		await this.request<void>(`/api/channels/${channelId}`, {
			method: 'DELETE'
		});
	}

	// Message endpoints
	async getMessages(
		channelId: number,
		page = 1,
		limit = 50
	): Promise<{ messages: Message[] }> {
		const params = new URLSearchParams({
			page: page.toString(),
			limit: limit.toString()
		});
		return this.request<{ messages: Message[] }>(`/api/channels/${channelId}/messages?${params}`);
	}

	async sendMessage(channelId: number, content: string, replyToId?: number): Promise<Message> {
		const messageData: { content: string; replyToId?: number } = { content };
		if (replyToId) {
			messageData.replyToId = replyToId;
		}

		const response = await this.request<{ success: boolean; message: string; data: any }>(`/api/channels/${channelId}/messages`, {
			method: 'POST',
			body: JSON.stringify(messageData)
		});

		// Return the message data from the response
		return response.data;
	}

	async updateMessage(messageId: number, content: string): Promise<Message> {
		return this.request<Message>(`/api/messages/${messageId}`, {
			method: 'PUT',
			body: JSON.stringify({ content })
		});
	}

	async deleteMessage(messageId: number): Promise<void> {
		await this.request<void>(`/api/messages/${messageId}`, {
			method: 'DELETE'
		});
	}

	// Admin endpoints
	async getUsers(): Promise<User[]> {
		const response = await this.request<{ success: boolean; data: User[] }>('/api/admin/users');
		return response.data;
	}

	async createUser(userData: Partial<User>): Promise<User> {
		return this.request<User>('/api/admin/users', {
			method: 'POST',
			body: JSON.stringify(userData)
		});
	}

	async updateUser(userId: number, updates: Partial<User>): Promise<User> {
		return this.request<User>(`/api/admin/users/${userId}`, {
			method: 'PUT',
			body: JSON.stringify(updates)
		});
	}

	async deleteUser(userId: number): Promise<void> {
		await this.request<void>(`/api/admin/users/${userId}`, {
			method: 'DELETE'
		});
	}

	async updateUserRole(userId: number, role: string): Promise<User> {
		return this.request<User>(`/api/admin/users/${userId}/role`, {
			method: 'POST',
			body: JSON.stringify({ role })
		});
	}

	async kickUser(userId: number, reason?: string): Promise<void> {
		await this.request<void>(`/api/admin/users/${userId}/kick`, {
			method: 'POST',
			body: JSON.stringify({ reason })
		});
	}

	async banUser(userId: number, reason?: string, duration?: number): Promise<void> {
		await this.request<void>(`/api/admin/users/${userId}/ban`, {
			method: 'POST',
			body: JSON.stringify({ reason, duration })
		});
	}

	async muteUser(userId: number, reason?: string, duration?: number): Promise<void> {
		await this.request<void>(`/api/admin/users/${userId}/mute`, {
			method: 'POST',
			body: JSON.stringify({ reason, duration })
		});
	}

	async unbanUser(userId: number): Promise<void> {
		await this.request<void>(`/api/admin/users/${userId}/unban`, {
			method: 'POST'
		});
	}

	async unmuteUser(userId: number): Promise<void> {
		await this.request<void>(`/api/admin/users/${userId}/unmute`, {
			method: 'POST'
		});
	}

	// Health check
	async healthCheck(): Promise<{ status: string; timestamp: string }> {
		return this.request<{ status: string; timestamp: string }>('/health');
	}

	// Admin endpoints
	async getAdminHealth(): Promise<unknown> {
		return this.request<unknown>('/api/admin/health');
	}

	async getMetrics(): Promise<unknown> {
		return this.request<unknown>('/api/admin/metrics');
	}

	async getOnlineUsers(): Promise<unknown[]> {
		return this.request<unknown[]>('/api/admin/users/online');
	}

	async getUserLatency(): Promise<unknown[]> {
		return this.request<unknown[]>('/api/admin/users/latency');
	}

	async getAuditLogs(limit?: number, offset?: number): Promise<unknown[]> {
		const params = new URLSearchParams();
		if (limit) params.append('limit', limit.toString());
		if (offset) params.append('offset', offset.toString());
		return this.request<unknown[]>(`/api/admin/logs?${params}`);
	}
}

// Export singleton instance
export const apiClient = new ApiClient();
export { ApiError };

import { browser } from '$app/environment';
// Use dynamic env import so build doesn't fail if variable is missing
const { PUBLIC_WS_URL } = import.meta.env;
import type { WebSocketEvent, TypingEvent, MessageEvent, UserEvent } from '$lib/types';

// Use relative URLs in browser to leverage Vite's proxy, fallback to direct URL
const WS_BASE_URL = browser ? '' : (PUBLIC_WS_URL || 'ws://localhost:8081');

type EventHandler<T = unknown> = (data: T) => void;

interface WebSocketOptions {
	url?: string;
	reconnectDelay?: number;
	maxReconnectAttempts?: number;
	heartbeatInterval?: number;
}

class WebSocketClient {
	private ws: WebSocket | null = null;
	private token: string | null = null;
	private url: string;
	private reconnectDelay: number;
	private maxReconnectAttempts: number;
	private heartbeatInterval: number;
	private reconnectAttempts = 0;
	private reconnectTimer: NodeJS.Timeout | null = null;
	private heartbeatTimer: NodeJS.Timeout | null = null;
	private isManualDisconnect = false;
	private isConnectingState = false;
	private eventHandlers = new Map<string, EventHandler[]>();

	constructor(options: WebSocketOptions = {}) {
		this.url = options.url || WS_BASE_URL;
		this.reconnectDelay = options.reconnectDelay || 1000;
		this.maxReconnectAttempts = options.maxReconnectAttempts || 5;
		this.heartbeatInterval = options.heartbeatInterval || 30000;
	}

	// Connection management
	connect(token: string): Promise<void> {
		if (!browser) {
			return Promise.reject(new Error('WebSocket only available in browser'));
		}

		if (this.ws?.readyState === WebSocket.OPEN) {
			return Promise.resolve();
		}

		this.token = token;
		this.isManualDisconnect = false;

		return new Promise((resolve, reject) => {
			try {
				this.isConnectingState = true;
				// Use relative URL in browser to leverage Vite's proxy
				const wsUrl = browser ? `/ws?token=${encodeURIComponent(token)}` : `${this.url}/ws?token=${encodeURIComponent(token)}`;
				console.log('Attempting WebSocket connection to:', wsUrl);
				this.ws = new WebSocket(wsUrl);

				this.ws.onopen = () => {
					console.log('WebSocket connection opened successfully');
					this.isConnectingState = false;
					this.reconnectAttempts = 0;
					this.startHeartbeat();
					this.emit('connected', { timestamp: new Date() });
					resolve();
				};

				this.ws.onmessage = (event) => {
					this.handleMessage(event);
				};

				this.ws.onclose = (event) => {
					console.log('WebSocket connection closed:', event.code, event.reason);
					this.isConnectingState = false;
					this.stopHeartbeat();
					this.emit('disconnected', { 
						code: event.code, 
						reason: event.reason,
						timestamp: new Date()
					});

					if (!this.isManualDisconnect) {
						this.attemptReconnect();
					}
				};

				this.ws.onerror = (error) => {
					console.error('WebSocket connection error:', error);
					this.isConnectingState = false;
					this.emit('error', { error, timestamp: new Date() });
					reject(new Error('WebSocket connection failed'));
				};

			} catch (error) {
				this.isConnectingState = false;
				reject(error);
			}
		});
	}

	disconnect(): void {
		this.isManualDisconnect = true;
		this.stopHeartbeat();
		if (this.reconnectTimer) {
			clearTimeout(this.reconnectTimer);
			this.reconnectTimer = null;
		}
		if (this.ws) {
			this.ws.close(1000, 'Manual disconnect');
			this.ws = null;
		}
	}

	private attemptReconnect(): void {
		if (this.isManualDisconnect || !this.token) {
			return;
		}

		if (this.reconnectAttempts >= this.maxReconnectAttempts) {
			this.emit('reconnectFailed', { attempts: this.reconnectAttempts });
			return;
		}

		const delay = this.reconnectDelay * Math.pow(2, this.reconnectAttempts);
		this.reconnectAttempts++;

		this.reconnectTimer = setTimeout(() => {
			if (!this.isManualDisconnect && this.token) {
				this.emit('reconnecting', { attempt: this.reconnectAttempts });
				this.connect(this.token!).catch(() => {
					// Reconnection failed, will try again
				});
			}
		}, delay);
	}

	private handleMessage(event: MessageEvent): void {
		try {
			console.log('ðŸ“¨ [WEBSOCKET] Raw message received:', event.data);
			const data = JSON.parse(event.data);
			console.log('ðŸ“¨ [WEBSOCKET] Parsed message:', data);

			// Handle different message types based on backend format
			switch (data.type) {
				case 'text': {
					// Text message from backend
					console.log('ðŸ’¬ [WEBSOCKET] Processing text message from backend:', {
						channelId: data.channel_id,
						userId: data.user_id,
						username: data.username,
						content: data.content?.substring(0, 50) + '...',
						messageId: data.data?.id
					});

					// Handle different message structures from backend
					let messageId = data.data?.id;
					let messageContent = data.content;
					let messageCreatedAt = data.data?.created_at || data.timestamp || new Date().toISOString();

					// If data is directly in the message (alternative structure)
					if (!messageId && data.data) {
						messageId = data.data.id;
						messageContent = data.data.content;
						messageCreatedAt = data.data.created_at || data.timestamp || new Date().toISOString();
					}

					// Fallback to timestamp if no ID
					if (!messageId) {
						messageId = Date.now();
						console.log('âš ï¸ [WEBSOCKET] No message ID found, using timestamp:', messageId);
					}

					const messageEvent = {
						type: 'message_created',
						channelId: Number(data.channel_id), // Ensure it's a number
						message: {
							id: messageId,
							content: messageContent,
							channelId: Number(data.channel_id), // Ensure it's a number
							authorId: data.user_id,
							author: {
								id: data.user_id,
								username: data.username
							},
							createdAt: messageCreatedAt,
							updatedAt: messageCreatedAt
						}
					} as MessageEvent;

					this.emit('message', messageEvent);
					break;
				}

				case 'join':
				case 'leave':
					// User joined/left channel
					console.log('ðŸ‘¥ [WEBSOCKET] User event:', {
						type: data.type,
						userId: data.user_id,
						username: data.username,
						channelId: data.channel_id
					});
					this.emit('user', {
						type: data.type === 'join' ? 'user_joined' : 'user_left',
						user: {
							id: data.user_id,
							username: data.username
						},
						channel_id: data.channel_id
					} as UserEvent);
					break;

				case 'typing':
				case 'stop_typing':
					// Typing indicators
					console.log('âŒ¨ï¸ [WEBSOCKET] Typing event:', {
						type: data.type,
						userId: data.user_id,
						channelId: data.channel_id
					});
					this.emit('typing', {
						userId: data.user_id,
						channelId: data.channel_id,
						isTyping: data.type === 'typing'
					} as TypingEvent);
					break;

				case 'heartbeat':
					// Handle heartbeat response
					console.log('ðŸ’“ [WEBSOCKET] Heartbeat received');
					break;

				case 'pong':
					// Handle pong response (heartbeat reply)
					console.log('ðŸ’“ [WEBSOCKET] Pong received');
					break;

				default:
					console.log('â“ [WEBSOCKET] Unknown message type:', data.type);
					this.emit('unknown', data);
					break;
			}
		} catch (error) {
			console.error('âŒ [WEBSOCKET] Failed to parse WebSocket message:', error);
			console.error('âŒ [WEBSOCKET] Raw message was:', event.data);
			this.emit('error', { error: 'Failed to parse WebSocket message', timestamp: new Date() });
		}
	}

	private startHeartbeat(): void {
		this.stopHeartbeat();
		this.heartbeatTimer = setInterval(() => {
			if (this.ws?.readyState === WebSocket.OPEN) {
				this.ws.send(JSON.stringify({ type: 'heartbeat' }));
			}
		}, this.heartbeatInterval);
	}

	private stopHeartbeat(): void {
		if (this.heartbeatTimer) {
			clearInterval(this.heartbeatTimer);
			this.heartbeatTimer = null;
		}
	}

	// Message sending
	sendMessage(channelId: number, content: string, replyToId?: number): void {
		if (this.ws?.readyState === WebSocket.OPEN) {
			const message = {
				type: 'text',
				channel_id: channelId,
				content: content,
				reply_to_id: replyToId
			};
			this.ws.send(JSON.stringify(message));
		}
	}

	joinChannel(channelId: number): void {
		if (this.ws?.readyState === WebSocket.OPEN) {
			const message = {
				type: 'join',
				channel_id: channelId
			};
			this.ws.send(JSON.stringify(message));
		}
	}

	leaveChannel(channelId: number): void {
		if (this.ws?.readyState === WebSocket.OPEN) {
			const message = {
				type: 'leave',
				channel_id: channelId
			};
			this.ws.send(JSON.stringify(message));
		}
	}

	startTyping(channelId: number): void {
		if (this.ws?.readyState === WebSocket.OPEN) {
			const message = {
				type: 'typing',
				channel_id: channelId
			};
			this.ws.send(JSON.stringify(message));
		}
	}

	stopTyping(channelId: number): void {
		if (this.ws?.readyState === WebSocket.OPEN) {
			const message = {
				type: 'stop_typing',
				channel_id: channelId
			};
			this.ws.send(JSON.stringify(message));
		}
	}

	// Event handling
	on<T = unknown>(event: string, handler: EventHandler<T>): void {
		if (!this.eventHandlers.has(event)) {
			this.eventHandlers.set(event, []);
		}
		this.eventHandlers.get(event)!.push(handler as EventHandler);
	}

	off(event: string, handler: EventHandler): void {
		const handlers = this.eventHandlers.get(event);
		if (handlers) {
			const index = handlers.indexOf(handler);
			if (index > -1) {
				handlers.splice(index, 1);
			}
		}
	}

	private emit<T = unknown>(event: string, data: T): void {
		const handlers = this.eventHandlers.get(event);
		if (handlers) {
			handlers.forEach(handler => {
				try {
					handler(data);
				} catch (error) {
					// Silently handle event handler errors
				}
			});
		}
	}

	// Status methods
	isConnected(): boolean {
		return this.ws?.readyState === WebSocket.OPEN;
	}

	isConnecting(): boolean {
		return this.isConnectingState;
	}

	getReconnectAttempts(): number {
		return this.reconnectAttempts;
	}
}

// Export singleton instance
export const wsClient = new WebSocketClient();

import { writable, derived, get } from 'svelte/store';
import { browser } from '$app/environment';
import type { User, Server, Channel, Message, ChatState, TypingEvent, AppState } from '$lib/types';
import { apiClient, ApiError } from '$lib/api/client';
import { wsClient } from '$lib/api/websocket';

// Initial app state
const initialState: AppState = {
	currentUser: null,
	currentServer: null,
	currentChannel: null,
	servers: [],
	isConnected: false,
	isLoading: false
};

const initialChatState: ChatState = {
	messages: [],
	typingUsers: [],
	isLoadingMessages: false,
	hasMoreMessages: true,
	replyingTo: null,
	selectedMessage: null,
	currentChannelId: undefined
};

// Create app store
export const appStore = writable<AppState>(initialState);

// Create stores
export const chatStore = writable<ChatState>(initialChatState);

// Derived stores for easier access
export const currentUser = derived(appStore, ($app) => $app.currentUser);
export const currentServer = derived(appStore, ($app) => $app.currentServer);
export const currentChannel = derived(appStore, ($app) => $app.currentChannel);
export const servers = derived(appStore, ($app) => $app.servers);
export const isConnected = derived(appStore, ($app) => $app.isConnected);
export const isLoading = derived(appStore, ($app) => $app.isLoading);
export const messages = derived(chatStore, ($chat) => $chat.messages);
export const typingUsers = derived(chatStore, ($chat) => $chat.typingUsers);

// App actions
export const appActions = {
	// Set current user
	setCurrentUser(user: User | null): void {
		appStore.update(state => ({
			...state,
			currentUser: user
		}));
	},

	// Set current server
	setCurrentServer(server: Server | null): void {
		appStore.update(state => ({
			...state,
			currentServer: server,
			currentChannel: null // Clear channel when server changes
		}));
	},

	// Set current channel
	setCurrentChannel(channel: Channel | null): void {
		appStore.update(state => ({
			...state,
			currentChannel: channel
		}));
	},

	// Set servers list
	setServers(serversList: Server[]): void {
		appStore.update(state => ({
			...state,
			servers: serversList
		}));
	},

	// Add server to list
	addServer(server: Server): void {
		appStore.update(state => ({
			...state,
			servers: [...state.servers, server]
		}));
	},

	// Update server in list
	updateServer(serverId: number, updates: Partial<Server>): void {
		appStore.update(state => ({
			...state,
			servers: state.servers.map(server => 
				server.id === serverId ? { ...server, ...updates } : server
			)
		}));
	},

	// Remove server from list
	removeServer(serverId: number): void {
		appStore.update(state => ({
			...state,
			servers: state.servers.filter(server => server.id !== serverId),
			currentServer: state.currentServer?.id === serverId ? null : state.currentServer,
			currentChannel: state.currentChannel?.serverId === serverId ? null : state.currentChannel
		}));
	},

	// Set connection status
	setConnectionStatus(connected: boolean): void {
		appStore.update(state => ({
			...state,
			isConnected: connected
		}));
	},

	// Set loading state
	setLoading(loading: boolean): void {
		appStore.update(state => ({
			...state,
			isLoading: loading
		}));
	},

	// Reset app state (for logout)
	reset(): void {
		appStore.set(initialState);
	},

	// Initialize app with user data
	initialize(user: User): void {
		appStore.update(state => ({
			...state,
			currentUser: user,
			isConnected: true
		}));
	}
};

// Chat actions
export const chatActions = {
	// Load messages for a channel
	async loadMessages(channelId: number, limit = 50, before?: number): Promise<void> {
		console.log('Loading messages for channel:', channelId);
		chatStore.update(state => ({ 
			...state, 
			isLoadingMessages: true,
			currentChannelId: channelId
		}));

		try {
			const response = await apiClient.getMessages(channelId, 1, limit);

			// Handle the actual backend response format
			const messages = response.messages || response.data || [];

			chatStore.update(state => ({
				...state,
				messages: before 
					? [...messages.reverse(), ...state.messages]
					: messages.reverse(),
				isLoadingMessages: false,
				hasMoreMessages: response.hasMore || false,
				currentChannelId: channelId
			}));
		} catch (error) {
			console.error('Failed to load messages:', error);

			chatStore.update(state => ({
				...state,
				isLoadingMessages: false
			}));
		}
	},

	// Send message
	async sendMessage(channelId: number, content: string, replyToId?: number): Promise<void> {
		try {
			// Try WebSocket first for real-time sending
			if (wsClient.isConnected()) {
				wsClient.sendMessage(channelId, content);
			} else {
				// Fallback to HTTP API
				const message = await apiClient.sendMessage(channelId, { content, replyToId });
				this.addMessage(message);
			}
		} catch (error) {
			console.error('Failed to send message:', error);
			throw error;
		}
	},

	// Add message to chat
	addMessage(message: Message): void {
		const currentState = get(chatStore);

		// Only add message if it's for the current channel
		if (currentState.currentChannelId === undefined) {
			return;
		}

		if (currentState.currentChannelId !== message.channelId) {
			return;
		}

		chatStore.update(state => ({
			...state,
			messages: [...state.messages, message].sort((a, b) => 
				new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime()
			)
		}));
	},

	// Update typing users
	updateTypingUsers(typingEvent: TypingEvent): void {
		chatStore.update(state => {
			const existingIndex = state.typingUsers.findIndex(
				user => user.userId === typingEvent.userId && user.channelId === typingEvent.channelId
			);

			if (typingEvent.isTyping) {
				// Add or update typing user
				if (existingIndex >= 0) {
					state.typingUsers[existingIndex] = typingEvent;
				} else {
					state.typingUsers.push(typingEvent);
				}
			} else {
				// Remove typing user
				if (existingIndex >= 0) {
					state.typingUsers.splice(existingIndex, 1);
				}
			}

			return {
				...state,
				typingUsers: [...state.typingUsers]
			};
		});
	},

	// Clear chat state
	clearChat(): void {
		chatStore.set(initialChatState);
	},

	// Set current channel ID
	setCurrentChannel(channelId: number): void {
		chatStore.update(state => ({
			...state,
			currentChannelId: channelId
		}));
	}
};

