		// Only add message if it's for the current channel
		if (currentChannelId !== messageChannelId) {
			console.log('ðŸ”´ [CHAT STORE] Message is for different channel, ignoring:', {
				messageChannelId: messageChannelId,
				currentChannelId: currentChannelId,
				messageChannelIdType: typeof message.channelId,
				currentChannelIdType: typeof currentState.currentChannelId
			});
			return;
		}

		// Check if message already exists (to prevent duplicates)
		const existingMessage = currentState.messages.find(m => m.id === message.id);
		if (existingMessage) {
			console.log('âš ï¸ [CHAT STORE] Message already exists, skipping:', message.id);
			return;
		}

		console.log('âœ… [CHAT STORE] Adding message to current channel');
		chatStore.update(state => {
			const newMessages = [...state.messages, message].sort((a, b) => 
				new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime()
			);
			console.log('âœ… [CHAT STORE] Updated messages count:', newMessages.length);
			return {
				...state,
				messages: newMessages
			};
		});
	},

	// Update existing message
	updateMessage(messageId: number, updates: Partial<Message>): void {
		chatStore.update(state => ({
			...state,
			messages: state.messages.map(msg => 
				msg.id === messageId ? { ...msg, ...updates } : msg
			)
		}));
	},

	// Delete message
	deleteMessage(messageId: number): void {
		chatStore.update(state => ({
			...state,
			messages: state.messages.filter(msg => msg.id !== messageId)
		}));
	},

	// Add reaction to message
	addReaction(messageId: number, emoji: string, userId: number): void {
		chatStore.update(state => ({
			...state,
			messages: state.messages.map(msg => {
				if (msg.id === messageId) {
					const reactions = msg.reactions || [];
					const existingReaction = reactions.find(r => r.emoji === emoji);

					if (existingReaction) {
						// Add user to existing reaction
						return {
							...msg,
							reactions: reactions.map(r => 
								r.emoji === emoji 
									? { 
										...r, 
										count: r.count + 1,
										users: [...r.users, { id: userId } as any]
									}
									: r
							)
						};
					} else {
						// Create new reaction
						return {
							...msg,
							reactions: [...reactions, {
								id: Date.now(),
								emoji,
								count: 1,
								users: [{ id: userId } as any],
								messageId
							}]
						};
					}
				}
				return msg;
			})
		}));
	},

	// Remove reaction from message
	removeReaction(messageId: number, emoji: string, userId: number): void {
		chatStore.update(state => ({
			...state,
			messages: state.messages.map(msg => {
				if (msg.id === messageId && msg.reactions) {
					return {
						...msg,
						reactions: msg.reactions.map(r => {
							if (r.emoji === emoji) {
								const newUsers = r.users.filter(u => u.id !== userId);
								return {
									...r,
									count: newUsers.length,
									users: newUsers
								};
							}
							return r;
						}).filter(r => r.count > 0)
					};
				}
				return msg;
			})
		}));
	},

	// Set reply target
	setReplyTo(message: Message | null): void {
		chatStore.update(state => ({
			...state,
			replyingTo: message
